///<summary>represent Internal session flag data</summary>
class IGKFv {
    private $_;
    static $sm_def;
    ///<summary>Represente __construct function</summary>
    public function __construct(){
        $this->
        _=array();
    }
    ///<summary>Represente __get function</summary>
    ///<param name="n"></param>
    public function __get($n){
        return $this->getFlag($n);
    }
    ///<summary>Represente __set function</summary>
    ///<param name="n"></param>
    ///<param name="v"></param>
    public function __set($n, $v){
        $this->setFlag($n, $v);
        return $this;
    }
    ///<summary>Represente __sleep function</summary>
    public function __sleep(){
        if(count($this->_) == 0){
            return array();
        }
        else{
            return array('_');
        }
    }
    ///<summary>Represente __wakeup function</summary>
    public function __wakeup(){
        if($this->_ == null)
            $this->
        _=array();
    }
    ///<summary>Represente Clear function</summary>
    public function Clear(){
        $this->
        _=array();
    }
    ///<summary>Represente Create function</summary>
    ///<param name="classname"></param>
    ///<param name="tab" ref="true"></param>
    static public function Create($classname, & $tab){
        if(isset(self::$sm_def[$classname])){
            igk_die("- already created for {$classname} -");
        }
        $o=new IGKFv();
        $o->
        _=& $tab;
        self::
        $sm_def[$classname]=$o;
        return $o;
    }
    ///<summary>free the flag if test ok</summary>
    public function freeFlag($code, $force=0){
        $g=$this->getFlag($code);
        if($force || ($g == null) || ((is_array($g) && (count($g) == 0)))){
            $this->unsetFlag($code);
        }
    }
    ///<summary>Represente Get function</summary>
    ///<param name="classname"></param>
    static public function Get($classname){
        if(isset(self::$sm_def[$classname])){
            return self::$sm_def[$classname];
        }
        return null;
    }
    ///<summary>get the flag.use explicitly setFlag to store reference data</summary>
    public function & getFlag($code, & $default=null, $register=0){
        $g=null;
        if(isset($this->_[$code]))
            $g=& $this->_[$code];
        else{
            if($register && ($default !== null)){
                $g=& $default;
                $this->
                _[$code]=& $g;
            }
            else{
                return $default;
            }
        }
        return $g;
    }
    ///<summary>Represente IsEmpty function</summary>
    public function IsEmpty(){
        return count($this->_) == 0;
    }
    ///<summary>Represente setFlag function</summary>
    ///<param name="code"></param>
    ///<param name="v"></param>
    public function setFlag($code, $v){
        if(func_num_args() < 2){
            igk_die("Argument count");
        }
        if($v === null)
            $this->unsetFlag($code);
        else{
            if(is_array($v))
                $this->
            _[$code]=& $v;
            else
                $this->
            _[$code]=$v;
        }
    }
    ///<summary>Represente unsetFlag function</summary>
    ///<param name="code"></param>
    public function unsetFlag($code){
        unset($this->_[$code]);
    }
    ///<summary>Represente updateFlag function</summary>
    ///<param name="code"></param>
    ///<param name="v"></param>
    public function updateFlag($code, $v){
        $this->setFlag($code, $v);
        $this->freeFlag($code);
    }
}
