///<summary>represent event properties</summary>
class IGKHtmlEventProperty implements IIGKHtmlGetValue , ArrayAccess {
    private $_n;
    protected $_p;
    ///<summary>Represente __construct function</summary>
    ///<param name="name"></param>
    protected function __construct($name){
        $this->
        _n=$name;
        $this->
        _p=[];
    }
    ///<summary>Represente __get function</summary>
    ///<param name="n"></param>
    public function __get($n){
        return igk_getv($this->_p, $n);
    }
    ///<summary>Represente __set function</summary>
    ///<param name="n"></param>
    ///<param name="v"></param>
    public function __set($n, $v){
        switch(strtolower($n)){
            case '@__callback':
            $this->
            $n=$v;
            return;
            case 'content':
            $this->
            _p=$v;
            return;
        }
        if($v == null){
            unset($this->_p[$n]);
            return $this;
        }
        $this->
        _p[$n]=$v;
    }
    ///<summary>Represente __toString function</summary>
    public function __toString(){
        return get_class($this). " : ".$this->_n;
    }
    ///<summary>Represente CreateEventProperty function</summary>
    ///<param name="name"></param>
    static public function CreateEventProperty($name){
        return new IGKHtmlEventProperty($name);
    }
    ///<summary>Represente getid function</summary>
    public function getid(){
        return $this->_n;
    }
    ///<summary>override this to get vavlue</summary>
    public function getValue($options=null){
        $s="";
        if(is_string($this->_p)){
            $s=$this->_p;
        }
        else{
            foreach($this->_p as $k=>$v){
                $s .= $k."=".$v.";";
            }
        }
        if(empty($s))
            return "";
        return "[".$this->_n. "]=\"".IGKHtmlUtils::GetAttributeValue($s)."\"";
    }
