///<summary>Represente class: IGKHtmlScriptAssocInfo</summary>
final class IGKHtmlScriptAssocInfo implements Serializable, ArrayAccess{
    private $data;
    static $sm_initCache;
    static $sm_store;
    ///<summary>Represente __construct function</summary>
    public function __construct(){
        $this->
        data=array();
    }
    ///<summary>Represente GetCacheFile function</summary>
    static public function GetCacheFile(){
        return igk_io_dir(igk_io_cachedir()."/.core.scripts.cache");
    }
    ///<summary>Represente initialize function</summary>
    public function initialize(){
        $cache=self::GetCacheFile();
        if(!IGKServerInfo::IsLocal() && file_exists($cache)){
            $data=& $this->data;
            include($cache);
            self::
            $sm_initCache=1;
            $c=igk_count($data);
            foreach($data as $k=>$v){
                if(!file_exists($k)){
                    unset($data[$k]);
                    self::
                    $sm_store=1;
                }
            }
        }
        else{
            if(igk_current_context() == "running"){
                self::
                $sm_initCache=1;
                igk_js_init_doc(igk_app()->Doc);
            }
        }
    }
    ///<summary>Represente offsetExists function</summary>
    ///<param name="k"></param>
    function offsetExists($k){
        return isset($this->data[$k]);
    }
    ///<summary>Represente offsetGet function</summary>
    ///<param name="i"></param>
    function offsetGet($i){
        return igk_getv($this->data, $i);
    }
    ///<summary>Represente offsetSet function</summary>
    ///<param name="k"></param>
    ///<param name="v"></param>
    function offsetSet($k, $v){
        $this->
        data[$k]=$v;
        self::
        $sm_store=1;
    }
    ///<summary>Represente offsetUnset function</summary>
    ///<param name="k"></param>
    function offsetUnset($k){
        unset($this->data[$k]);
        self::
        $sm_store=1;
    }
    ///<summary>Represente serialize function</summary>
    public function serialize(){
        static $caching=null;
        if(self::$sm_store){
            if(!$caching){
                $cache=self::GetCacheFile();
                if(!($cdata=igk_get_env("sys://res_files"))){
                    $cdata=$this->data;
                }
                $o="<?php\n";
                foreach($cdata as $k=>$v){
                    $o .= '$data["'.$k.'"]='.$v.';'."\n";
                }
                igk_io_w2file($cache, $o);
                $caching=1;
            }
            self::
            $sm_store=0;
        }
        return null;
    }
    ///<summary>Represente toArray function</summary>
    public function toArray(){
        return $this->data;
    }
    ///<summary>Represente unserialize function</summary>
    ///<param name="s"></param>
    public function unserialize($s){
        igk_wln_e("unserialize .....");
    }
}
