            if(is_array($s)){
                $d=array_merge($d, $s);
                foreach($s as $k){
                    $d=array_merge($d, igk_getAllChilds($k));
                }
            }
        }
        return $d;
    }
    ///<summary>Represente GetAttributeValue function</summary>
    ///<param name="c"></param>
    ///<param name="context" default="null"></param>
    static public function GetAttributeValue($c, $context=null){
        $s=self::GetValue($c);
        $q=trim($s);
        $v_h="\"";
        if(preg_match("/^\'/", $q) && preg_match("/\'$/", $q)){
            $v_h="'";
        }
        if(IGKString::StartWith($q, $v_h)){
            $q=substr($q, 1);
        }
        if(IGKString::EndWith($q, $v_h)){
            $q=substr($q, 0, strlen($q) - 1);
        }
        if($v_h == "\""){
            $q=str_replace("\"", "&quot;", $q);
        }
        if($context && is_string($context) && (preg_match("/(xml|xsl)/i", $context))){
            $q=str_replace("&amp;", "&", $q);
        }
        return $q;
    }
    ///<summary>Represente GetContentValue function</summary>
    ///<param name="n"></param>
    ///<param name="options" default="null"></param>
    static public function GetContentValue($n, $options=null){
        if($n->iscallback("handleRender")){
            return $n->handleRender();
        }
        $c=$n->getContent();
        $inner="";
        if(igk_is_callback_obj($c)){
            return igk_invoke_callback_obj(igk_getv($c, 'clType') == 'node' ? $n: null, $c);
        }
        if(is_array($c)){
            $r=igk_createnode('content');
            $i=0;
            foreach($c as $k=>$v){
                if(ord($k) == 0)
                    $r->add("item")->setAttribute("key", $k)->setContent($v);
                $i++;
            }
            return $r->getinnerHTML(null);
        }
        if(is_object($c) && igk_reflection_class_extends($c, "IGKHtmlItem")){
            if(self::$gRendering === $c)
                return "";
            self::
            $gRendering=$c;
            $o=$c->Render($options);
            self::
            $gRendering=null;
            return $o;
        }
        return self::GetValue($c, $options);
    }
    ///<summary>Represente GetTableFromSingleArray function</summary>
    ///<param name="array"></param>
    static public function GetTableFromSingleArray($array){
        $tab=igk_createnode("table");
        foreach($array as $k=>$v){
            $tr=$tab->addTr();
            $tr->addTd()->
            Content=$k;
            $tr->addTd()->
            Content=$v;
        }
        return $tab;
    }
    ///<summary>return value according to string</summary>
    static public function GetValue($c, $options=null){
        $out=IGK_STR_EMPTY;
        if(($c == "0") || (is_numeric($c) && ($c === "0")))
            return "0";
        if(is_numeric($c) || (is_string($c) && !empty($c))){
            $out .= $c;
        }
        else if(is_object($c)){
            $cl=get_class($c);
            if(igk_reflection_class_extends($cl, 'IGKHtmlItemBase')){
                return igk_html_render_node($c, $options, null);
            }
            while(is_object($c)){
                $c=self::GetValueObj($c, $options);
            }
            if(empty($c))
                return null;
            $out .= $c;
        }
        return $out;
    }
    ///<summary>Represente GetValueObj function</summary>
    ///<param name="v"></param>
    ///<param name="options"></param>
    static public function GetValueObj($v, $options){
        if(method_exists(get_class($v), IGK_FC_GETVALUE)){
            $v=$v->getValue($options);
        }
        else{
            switch(igk_getv($v, IGK_OBJ_TYPE_FD)){
                case 'callable':
                $v=call_user_func_array($v->name, $v->attrs ? $v->attrs: array());
                break;
                case '_callback':
                $v=igk_invoke_callback_obj(null, $v);
                break;default: 
                if(is_callable($fc=igk_getv($v, "callback"))){
                    $v=(call_user_func_array($fc, array_merge(igk_getv($v, "params", []), $options ? [$options]: [])));
                }
                else{
                    $v="\"IGK:DATAOBJ\"";
                }
                break;
            }
        }
        return $v;
    }
    ///<summary>Represente MoveChilds function</summary>
    ///<param name="item"></param>
    ///<param name="target"></param>
    static public function MoveChilds($item, $target){
        if(($item == null) || ($target == null) || !$item->HasChilds)
            return false;
        foreach($item->getChilds() as $k){
            IGKHtmlUtils::AddItem($k, $target);
        }
        return true;
    }
