    ///<summary>Represente ShowHierarchi function</summary>
    ///<param name="var"></param>
    static public function ShowHierarchi($var){
        $out=IGK_STR_EMPTY;
        if($var->HasChilds){
            $out .= "<ul>";
            foreach($var->Childs as $k){
                $out .= "<li>".$k->TagName;
                if($k->TagName == "a")
