    ///<summary>REMOVE FOLDER</summary>
    static public function RmDir($dir, $recursive=true){
        if(!is_dir($dir))
            return false;
        $pdir=array($dir);
        $kdir=array($dir);
        $d=1;
        while($dir=array_pop($pdir)){
            $hdir=opendir($dir);
            if(!$hdir)
                return false;
            while(($f=readdir($hdir))){
                if(($f == ".") || ($f == ".."))
                    continue;
                $v=igk_io_dir($dir."/".$f);
                if(is_dir($v)){
                    if($recursive){
                        array_push($pdir, $v);
                        array_push($kdir, $v);
                    }
                    else{
                        $d=0;
                        break;
                    }
                }
                else if(is_file($v)){
                    if($recursive)
                        unlink($v);
                    else{
                        $d=0;
                        break;
                    }
                }
            }
            closedir($hdir);
        }
        while($d && ($dir=array_pop($kdir))){
            rmdir($dir);
        }
        return igk_count($kdir) == 0;
    }
