                $v_list[$n]=$v;
                if(isset($v_rlist[$n])){
                    foreach($v_rlist[$n] as $tk=>$tv){
                        $v->add($tv);
                    }
                    unset($v_rlist[$n]);
                }
                else{
                    if(isset($v_rlist[$p])){
                        $tb=$v_rlist[$p];
                        $tb[]=$v;
                    }
                    else
                        $tb=array($v);
                    $v_rlist[$p]=$tb;
                }
            }
        }
        igk_usort($tab, array("IGKMenuItem", "SortMenuByIndex"));
        foreach($tab as $t=>$m){
            if($m->MenuParent == null){
                $menus[$m->
                Name]=self::InitMenu($targetNode, $m, $page);
