    static public function GetMenuLevel($menu){
        $q=$menu->MenuParent;
        $i=0;
        while($q !== null){
            $q=$q->MenuParent;
            $i++;
        }
        return $i;
    }
    ///<summary>get parent name of  the menu</summary>
    static public function GetParentName($name){
        $t=explode(".", $name);
        $c=count($t);
        if($c > 1){
            $out=IGK_STR_EMPTY;
            $v_sep=false;
            for($i=0; $i < $c-1; $i++){
                if($v_sep)
                    $out .= ".";
                else
                    $v_sep=true;
                $out .= $t[$i];
            }
            return $out;
        }
        return null;
    }
    ///<summary>Represente InitMenu function</summary>
    ///<param name="target"></param>
    ///<param name="menu"></param>
    ///<param name="pages" ref="true"></param>
    static public function InitMenu($target, $menu, & $pages){
        $add_uri=null;
        $node=null;
        if(isset($page)){
            $page=strtolower($menu->CurrentPage);
            if(isset($pages [$page])){
                if(!is_array($pages [$page])){
                    $cp=$pages [$page];
                    $cp->
                    PageIndex=0;
                    $pages[$page]=array($cp);
                    $cp->
                    MenuItem["href"]=$cp->MenuItem["href"];
                }
                $pages [$page][]=$menu;
                $menu->
                PageIndex=count($pages [$page])-1;
                $add_uri="&pageindex=".$menu->PageIndex;
                if($menu->MenuParent){
                    $add_uri=strtolower("&v=".substr($menu->Name, strlen($menu->MenuParent->Name) + 1));
                }
            }
            else{
                $pages [$page]=$menu;
            }
        }
        if($add_uri)
            ;
