///<summary> Number operator class </summary>
final class IGKNumber {
    static $sm_sizeFormat=array(
            "Tb"=>1099511627776,
            "Gb"=>1073741824,
            "Mb"=>1048576,
            "Kb"=>1024,
            "B"=>1
        );
    ///<summary>Represente __GetValue function</summary>
    ///<param name="d"></param>
     static private function __GetValue($d){
        if(is_int($d) || preg_match("/[0-9]/i", $d)){
            return $d;
        }
        else
            return 10 + (ord($d) - ord('A'));
    }
    ///<summary>Represente FromBase function</summary>
    ///<param name="d"></param>
    ///<param name="base"></param>
    static public function FromBase($d, $base){
        $o=0;
        $v=strtoupper(IGK_STR_EMPTY.$d);
        $ln=strlen($v);
        for($i=0; $i < $ln; $i++){
            $h=self::__GetValue($v[$ln - $i-1]);
            $o += pow($base, $i) * $h;
        }
        return $o;
    }
    ///<summary>Represente GetMemorySize function</summary>
    ///<param name="value"></param>
    ///<param name="round" default="4"></param>
    static public function GetMemorySize($value, $round=4){
        if($value == 0)
            return "0 byte";
        foreach(self::$sm_sizeFormat as $k=>$v){
            if($value > $v){
                return round(($value/$v), $round)." ".__("enum.sizeUnit.".$k);
            }
        }
        return "0 byte";
    }
    ///<summary>Represente HexP function</summary>
    ///<param name="r"></param>
    static private function HexP($r){
        $g=($r>=10) ? chr(ord("A") + ($r-10)): $r;
        return $g;
    }
    ///<summary>Represente ToBase function</summary>
    ///<param name="d"></param>
    ///<param name="base"></param>
    ///<param name="length" default="-1"></param>
    static public function ToBase($d, $base, $length=-1){
        if(is_numeric($d) == false)
            return "0";
        $o=IGK_STR_EMPTY;
        if($base > 0){
            if(is_string($d)){
                for($i=0; $i < strlen($d); $i++){
                    $th=ord($d[$i]);
                    $p=(int)($th/$base);
                    $r=($th % $base);
                    if($p < $base){
                        if($p != 0)
                            $o=self::HexP($p). self::HexP($r);
                        else
                            $o=self::HexP($r);
                    }
                    else{
                        $o=self::HexP($r). $o;
                        $o=self::ToBase($p, $base). $o;
                    }
                }
            }
            else{
                $p=(int)($d / $base);
                $r=$d % $base;
                if($p < $base){
                    if($p != 0)
                        $o=self::HexP($p). self::HexP($r);
                    else
                        $o=self::HexP($r);
                }
                else{
                    $o=self::HexP($r). $o;
                    $o=self::ToBase($p, $base). $o;
                }
            }
        }
        if($length != -1){
            for($i=strlen($o); $i < $length; $i++){
                $o="0". $o;
            }
        }
        return $o;
    }
}
