    ///<summary>Represente __checkDomain function</summary>
    private function __checkDomain(){
        $domain=$this->Session->Domain;
        $buri=$this->Configs->website_domain;
        if($domain !== $buri){
            if(!empty($domain)){
                $this->Session->
