class IGKColorf extends IGKObject {
    private $m_A;
    private $m_B;
    private $m_G;
    private $m_R;
    ///<summary>Represente __bindStringData function</summary>
    ///<param name="cl"></param>
    ///<param name="v"></param>
    static private function __bindStringData($cl, $v){
        $v=trim(strtoupper($v));
        if(IGKString::StartWith($v, "#") || IGKString::StartWith($v, "0x")){
            $v=str_replace("#", IGK_STR_EMPTY, $v);
            $v=str_replace("0x", IGK_STR_EMPTY, $v);
            $i=0;
            switch(strlen($v)){
                case 8:
                break;
                case 4:
                $v=IGK_STR_EMPTY. $v[0]. $v[0]. $v[1]. $v[1]. $v[2]. $v[2]. $v[3]. $v[3];
                break;
                case 6:
                $v="FF". $v;
                break;
                case 3:
                $v="FF". $v[0]. $v[0]. $v[1]. $v[1]. $v[2]. $v[2];
                break;
                default:
                break;
            }
            try {
                $i=IGKNumber::FromBase($v, 16);
                $r=(($i >> 16)& 0x00FF);
                $g=(($i >> 8)& 0x00FF);
                $b=(($i)& 0x00FF);
                $cl->
                m_R=$r / 255.0;
                $cl->
                m_G=$g / 255.0;
                $cl->
                m_B=$b / 255.0;
            }
            catch(Exception $ex){}
        }
    }
    ///<summary>Represente FromString function</summary>
    ///<param name="v"></param>
