        $t=igk_css_get_color_value($v);
        if(empty($t)){
            $cl=new IGKColorf();
            $cl->
            m_A=1.0;
            self::__bindStringData($cl, $v);
            return $cl;
        }
        $cl=new IGKColorf();
        $cl->
        m_A=1.0;
        self::__bindStringData($cl, $t);
        return $cl;
    }
