    public function getR(){
        return $this->m_R;
    }
    ///<summary>Represente loadw function</summary>
    ///<param name="v"></param>
    public function loadw($v){
        self::__bindStringData($this, $v);
    }
    ///<summary>Represente setA function</summary>
    ///<param name="value"></param>
    public function setA($value){
        if(($value>=0) && ($value<=1.0))
            $this->
        m_A=$value;
    }
    ///<summary>Represente setB function</summary>
    ///<param name="value"></param>
    public function setB($value){
        if(($value>=0) && ($value<=1.0))
