        $c=& $m->getFlag($key, $default, $register);
        return $c;
        $g=$this->getFlagParams();
        $c=null;
        if(isset($g[$key]))
            $c=& $g[$key];
        if($c === null){
            if(is_callable($default)){
                $c=$default();
                if(is_callable($c))
                    igk_die("/i\\ value not valid.  value return by a callable.", __METHOD__);
            }
            else{
                $c=$default;
            }
            if($register && ($c !== null)){
                $this->setParam($key, $c);
            }
        }
        return $c;
    }
