        igk_exit();
        if(!empty($sys) && preg_match("/^([0-9]+)\.([0-9]+)(\.([0-9]+)\.([0-9]+))?$/", $sys)){
            $p=igk_cmp_version($v, $sys);
            if($p == 0){
                $m=$r->add("Message");
                $m->addDiv()->
                Content=R::ngets("msg.youareuptodate");
