            else if($p == -1){
                $r->add("Status")->
                Content=1;
                $d=$r->add("Message")->addDiv();
                $d->setStyle("padding-top:10px; padding-bottom:10px");
                $u=igk_io_fullbaserequesturi()."/".$this->getUri('conf_install_update');
                $d->addDiv()->
                Content=R::ngets("msg.uptodaterequired");
                $d->addDiv()->
