                igk_html_build_select($td, $v."[]", IGKdbColumnDataType::GetDbTypes(), null, $k);
                break;default: 
                $i=$td->addInput($v."[]", "text", $k);
                $i["class"]="-cltext";
                $i["style"]="max-width:125px;";
                break;
            }
        }
        IGKHtmlUtils::AddImgLnk($tr->addTd(), $this->getUri("ca_dropfield&n=".$tr["__id"]), "drop_16x16");
