    ///<summary>Represente _getTables function</summary>
    ///<param name="searchkey" default="null"></param>
    private function _getTables($searchkey=null){
        $r=null;
        $mysql=igk_get_data_adapter($this, true);
        if($mysql){
            $mysql->connect($this->App->Configs->db_name);
            $r=$mysql->listTables();
            if($r){
                $tab=$r->CreateEmptyResult($r);
                $mysql->close();
                $n=$r->Columns[0]->name;
                if($searchkey != null){
                    $q=strtolower($searchkey);
                    foreach($r->Rows as $t){
                        if(strstr(strtolower($t->$n), $q)){
                            $tab->addRow(array("Table"=>$t->$n));
                        }
                    }
                    return $tab;
                }
                else{
                    foreach($r->Rows as $t){
                        $tab->addRow(array("Table"=>$t->$n));
                    }
                    return $tab;
                }
            }
        }
        return null;
    }
