    ///<summary>Represente _initTableInfo function</summary>
    private function _initTableInfo(){
        if(igk_get_env("sys://db_init")){
            return;}igk_set_env("sys://db_init", 1);
        $cf=$this->getCacheFile();
        if(!file_exists($cf)){
            $tables=array();
            $t=igk_sys_getall_ctrl();
            foreach($t as $k=>$v){
                if(igk_is_class_incomplete($v))
                    continue;
                if($v === $this)
                    continue;
                if($v->getUseDataSchema()){
                    $r=$v->loadDataFromSchemas();
                    if($r != null){
                        foreach($r as $kk=>$vv){
                            $this->_addTable($kk, $v);
                        }
                    }
                }
                else{
                    $r=$v->getDataTableInfo();
                    if($r != null){
                        $this->_addTable($v->getDataTableName(), $v);
                    }
                }
            }
            $this->_storeDbCache(1);
        }
        else{
            $this->_loadDbFromCache();
        }
        igk_set_env("sys://db_init", null);
    }
    ///<summary>Represente _loadDbFromCache function</summary>
    private function _loadDbFromCache(){
        $f=$this->getCacheFile();
        if($f && file_exists($f)){
            $txt=IGKIO::ReadAllText($f);
            $s=explode(IGK_LF, $txt);
            $t=array();
            $sk=array();
            foreach($s as $k=>$v){
                if(empty($v))
                    continue;
                $db=explode(":", $v);
                if(count($db) < 1)
                    continue;
                $n=$db[1];
                if(isset($sk[$n]))
                    $ctrl=$sk[$n];
                else{
                    $ctrl=igk_getctrl_from_classname($db[1]);
                    $sk[$n]=$ctrl;
                }
                if($ctrl){
                    $t[$db[0]]=$ctrl;
                }
                else{
                    igk_ilog("No ctrl found for ".$db[0], __FUNCTION__);
                }
            }
            $this->setParam("loadTables", $t);
        }
        else{
            $this->_initTableInfo();
        }
    }
