    private function _storeDbCache($reset=0){
        $f=$this->getCacheFile();
        $o="";
        $tables=$this->getParam("loadTables");
        if($tables){
            $kt=0;
            foreach($tables as $k=>$v){
                if($kt){
                    $o .= IGK_LF;
                }
                $o .= $k.":".get_class($v);
                $kt=1;
            }
        }
        igk_io_w2file($f, $o, true);
        if(!$reset)
            $this->setParam("loadTables", null);
    }
