    ///<summary>Represente sendmail function</summary>
    ///<param name="from"></param>
    ///<param name="to"></param>
    ///<param name="subject"></param>
    ///<param name="message"></param>
    ///<param name="reply" default="null"></param>
    ///<param name="attachement" default="null"></param>
    ///<param name="type" default="text/html"></param>
    public function sendmail($from, $to, $subject, $message, $reply=null, $attachement=null, $type="text/html"){
        $header=null;
        $this->initMailSetting();
        if($reply){
            ini_set("mail_from", $reply);
        }
        $mail=new IGKMail();
        $this->init_mail_config($mail);
        $mail->
        HtmlMsg=$message;
        $mail->
        Title=$subject;
        $mail->
        From=$from;
        $mail->
        HtmlCharset="utf-8";
        $mail->
        TextCharset="utf-8";
        $mail->addTo($to);
        if(is_array($attachement)){
            foreach($attachement as $k=>$v){
                if(igk_reflection_class_extends($v, "IGKMailAttachement")){
                    $mail->attach($v);
                }
                else
                    $mail->attachContent($v->Content, $v->ContentType, $v->CID);
            }
        }
        return $mail->sendMail();
    }
