            if($v_rootmenu){
                $this->
                m_menu_selected=$v_rootmenu->MenuItem;
                $this->
                m_menu_selected["class"]="+igk-menu_selected";
            }
        }
        else{
            if(IGKServerInfo::IsLocal()){
                igk_notifyctrl()->addError("[web_global_menu] not define [".$page."] - ". igk_io_request_uri());
            }
        }
    }
