    ///<summary>Represente _addLoadPicForm function</summary>
    ///<param name="div"></param>
    private function _addLoadPicForm($div){
        $frm=$div->addForm();
        $frm["action"]=$this->getUri("loadfile");
        $frm["method"]="POST";
