    ///<summary>Represente _storeData function</summary>
    private function _storeData(){
        $out=IGK_STR_EMPTY;
        $g=$this->getAllPics();
        foreach($g as $k=>$v){
            $out .= $k.igk_csv_sep().$v.IGK_LF;
        }
        if(igk_io_save_file_as_utf8($path=igk_io_syspath(self::DATAFILE), $out, true)){
            $e=["file"=>$path];
            igk_sys_regchange(self::PICRES_KEY, $e);
            return true;
        }
        return false;
    }
    ///<summary>Represente _support function</summary>
    ///<param name="ext"></param>
    private function _support($ext){
        $ext=$this->_getexts();
