    ///<summary>General connection method</summary>
    ///<param name="log">display login</param>
    ///<param name="pwd">clear pwd</param>
    public function connect($log=null, $pwd=null){
        $u=$this->App->Session->User;
        if($u != null)
            return false;
        $rm_me=null;
        if($log == null){
            if(!igk_server()->ispost()){
                return false;
            }
            $log=igk_getr("clLogin");
            $pwd=igk_getr("clPwd");
            $rm_me=igk_getr("remember_me");
            unset($_REQUEST["clPwd"]);
            if(empty($log) || empty($pwd)){
                return false;
            }
        }
        $e=$this->getDbEntries();
        if($e){
            if(!preg_match("/@(.)+$/i", $log)){
                $log=$log."@".$this->App->Configs->website_domain;
            }
            $tab=array("clLogin"=>$log, "clPwd"=>md5($pwd));
            $t=$e->searchEqual($tab);
            if($t && is_object($t)){
                if($t->clStatus == 1){
                    $t=igk_sys_create_user($t);
                    $this->setGlobalUser($t);
                    $u=$this->App->Session->User;
                    if($rm_me){
                        igk_user_store_tokenid($t);
                    }
                    return true;
                }
                else{
                    $this->app->Session->
                    ErrorString="[connectfailed] : status of the requested user is not activated";
                    return false;
                }
            }
        }
        else{
            igk_notifyctrl("login")->addWarningr("warn.connection.db.failed");
        }
        return false;
    }
    ///<summary>Represente connectpage function</summary>
    public function connectpage(){
        $u=$this->App->Session->User;
        if($u != null){
            igk_navto(igk_io_baseuri());
