        if($r){
            $tr=$table->addTr();
            $tr->add("th")->addSpace();
            $tr->add("th")->
            Content=R::ngets("lb.clFirstName");
            $tr->add("th")->
            Content=R::ngets("lb.clLastName");
            $tr->add("th")->setClass("fitw")->
            Content=R::ngets("lb.clLogin");
            $tr->add("th")->
            Content=R::ngets("lb.clLevel");
            $tr->add("th")->
            Content=R::ngets("lb.clStatus");
            $tr->add("th")->
            Content=R::ngets("lb.clDate");
            $tr->add("th")->
            Content=R::ngets("lb.clClassName");
            $tr->add("th")->addSpace();
            $tr->add("th")->addSpace();
            $tr->add("th")->addSpace();
            $selected=igk_getr("v", 1);
            $perpage=20;
            $max=$perpage;
            $count=$r->RowCount;
            $epagination=$max < $count;
            $it=new IGKIterator($r->Rows);
            $it->setRewindStart($perpage * ($selected - 1));
            $editor=igk_get_builder_engine(null, $table);
            $grpuri=$this->getUri("lstgrp&id=");
            foreach($it as $k=>$v){
