                Content=$v->clLevel;
                $tr->addTd()->
                Content=$v->clStatus;
                $tr->addTd()->
                Content=$v->clDate;
                $tr->addTd()->
                Content=$v->clClassName;
                IGKHtmlUtils::AddImgLnk($tr->addTd(), igk_js_post_frame($edit_uri), "edit_16x16");
