                $tr->addTd()->addDiv()->setClass("igk-svg-btn svg-16")->addAJXA($grpuri.$v->clId)->
                Content=igk_svg_use("group");
                $max--;
                if($max == 0)
                    break;
            }
            if($epagination){
                $pagination=$frm->addDiv()->addAJXPaginationView(igk_io_currenturi().'/'.$this->getUri("view&v="), $count, $perpage, $selected, "^.igk-cnf-content");
            }
        }
        if(igk_app()->Session->URI_AJX_CONTEXT){
            $t->RenderAJX();
            igk_exit();
        }
    }
