///<summary>Represente class: IGKPageControllerBase</summary>
abstract class IGKPageControllerBase extends IGKCtrlTypeBase{
    const PAGE_CONSTANT=IGKSession::BASE_SESS_PARAM + 0xA0;
    const PAGE_TEMPLATE=self::PAGE_CONSTANT + 2;
    const PAGE_USER=self::PAGE_CONSTANT + 1;
    ///<summary>Represente _initView function</summary>
    protected function _initView(){
        $this->register_autoload();
        parent::_initView();
    }
    ///<summary> autoload binding class </summary>
    protected function auto_load_class($n){
        $entryNS=$this->getEntryNameSpace() ?? "";
        if(empty($entryNS) || (strpos($n, $entryNS) === 0)){
            if(!empty($entryNS))
                $n=substr($n, strlen($entryNS));
            if(file_exists($file=igk_io_dir($this->getDeclaredDir()."/Lib/Classes/".$n.".php"))){
                include_once($file);
                return 1;
            }
        }
        if(file_exists($f=igk_html_uri(IGK_LIB_DIR."/Lib/".$n.".php"))){
            include_once($f);
            if(!class_exists($n, false)){
                igk_die("file loaded but not content class {$n} definition");
            }
            return 1;
        }
        return 0;
    }
    ///<summary>check init and init user to this apps </summary>
    protected function checkUser($nav=true, $uri=null){
        $r=true;
        $u=$this->App->Session->User;
        $ku=$this->User;
        if($ku == null){
            if($u != null){
                $this->
                User=$this->initUserFromSysUser($u);
            }
            else
                $r=false;
        }
        if($nav && !$r){
            $m=igk_io_base_request_uri();
            $s="";
            $u=($uri == null ? $this->getAppUri(""): $uri);
            if(!empty($m)){
                $s="q=".base64_encode($m);
                $u .= ((strpos($u, "?") === false) ? "?": "&").$s;
            }
            igk_navto($u);
        }
        return $r;
    }
    ///<summary>Represente dropDb function</summary>
    protected function dropDb(){
        $ctrl=$this;
        $func=function() use ($ctrl){
            $rdb=$ctrl->db;
            if($rdb && method_exists($rdb, "onStartDropTable")){
                $rdb->onStartDropTable();
            }
            igk_raise_event("sys://db/startdroptable", $ctrl);
        };
        if(!igk_getv($this->getConfigs(), "clDataSchema")){
            $db=igk_get_data_adapter($this, true);
            if($db && $db->connect()){
                $func();
                $db->dropTable($this->getDataTableName());
                $db->close();
            }
        }
        else{
            $tb=$this->loadDataFromSchemas();
            $db=igk_get_data_adapter($this, true);
            if($db){
                if($db->connect()){
                    $v_tblist=array();
                    foreach($tb as $k=>$v){
                        $v_tblist [$k]=$k;
                    }
                    $func();
                    $db->dropTable($v_tblist);
                    $db->close();
                }
            }
        }
    }
    ///<summary>Represente get_output function</summary>
    public function get_output(){
        $s=$this->getEnvParam("_output");
        return $s;
    }
    ///<summary> registered entry namespace . for auto load class </summary>
