    }
    ///<summary>Represente register_autoload function</summary>
    final protected function register_autoload(){
        $k="sys://autoloading/".get_class($this);
        if(igk_get_env($k))
            return;
        igk_set_env($k, 1);
        spl_autoload_register(array($this, "auto_load_class"));
    }
    ///<summary> reset only the current dataabase</summary>
    public function resetDb($navigate=1){
        igk_wln_e("call reset db");
        $s=igk_is_conf_connected() || igk_sys_isuser_authorize(igk_user(), $this->Name.":".__FUNCTION__);
        if(!$s){
            if(igk_app_is_uri_demand($this, __FUNCTION__)){
                igk_navto($this->getAppUri());
            }
            return;
        }
        igk_db_drop_ctrl_db($this, $this->loadDataFromSchemas(), __FUNCTION__);
        $ad=igk_get_data_adapter($this);
        $ad->initForInitDb();
        $this->initDb();
        $ad->flushForInitDb();
        $this->logout(0);
        if($navigate && igk_uri_is_match(igk_io_currenturi(), $this->getAppUri(__FUNCTION__))){
            igk_notification_push_event(IGK_NOTIFICATION_DB_CHANGED, $this, null);
            igk_navto($this->getAppUri());
            igk_exit();
        }
    }
    ///<summary>Represente set_output function</summary>
