final class IGKFrameDialogCtrl extends IGKControllerBase implements IIGKFrameController {
    ///<summary>Represente __construct function</summary>
    public function __construct(){
        parent::__construct();
    }
    ///<summary>Represente close_frame_ajx function</summary>
    public function close_frame_ajx(){
        $href=base64_decode(igk_getr("href"));
        $tag=igk_getquery_args($href);
        $this->closeFrame(igk_getv($tag, "id"));
        igk_wl($this->App->Doc->body->Render());
        igk_exit();
    }
    ///<summary>Represente closeAllFrame function</summary>
    public function closeAllFrame(){
        $c=array_keys($this->m_frames);
        $i=0;
        foreach($c as $k=>$v){
            igk_frame_close($v);
            $i++;
        }
        igk_navtocurrent();
    }
    ///<summary>Close frame</summary>
    ///<param name="id" default='null'>id of frame to close on server side</param>
    ///<param name="navigate" default='null'>navigation uri</param>
    public function closeFrame($id=null, $navigate=null){
        $v_id=($id != null) ? $id: igk_getr("id", 0);
        $closeuri=null;
        $navigate=$navigate === null ? igk_getr("navigate", false): $navigate;
        if(isset($this->m_frames[$v_id])){
            $frame=$this->m_frames[$v_id];
            $args=igk_getquery_args($frame->closeUri);
            if(($closeuri=urldecode(igk_getr("closeuri"))) == null)
                $closeuri=urldecode(igk_getv($args, "closeuri"));
            igk_html_rm($frame);
            if(method_exists(get_class($frame->Owner), "frameClosed")){
                $frame->Owner->frameClosed();
            }
            $frame->closeMethod();
            $frame->Dispose();
            unset($this->m_frames[$v_id]);
            unset($frame);
        }
        else{
            igk_debug_wln("Frame not found [".$v_id."]");
            return;
        }
        if(!igk_is_ajx_demand()){
            if($closeuri){
                igk_navtocurrent($closeuri);
                igk_exit();
            }
            else if($navigate){
                igk_navtocurrent();
                igk_exit();
            }
        }
    }
    ///<summary>Represente closeFrame_ajx function</summary>
    public function closeFrame_ajx(){
        $id=igk_getr("id");
        igk_frame_close($id);
    }
    ///<summary>Represente ContainFrame function</summary>
    ///<param name="id"></param>
    ///<param name="frame"></param>
    ///<param name="remove" default="true"></param>
    public function ContainFrame($id, $frame, $remove=true){
        if(isset($this->m_frames[$id])){
            if($frame !== $this->m_frames[$id]){
                igk_wln("contains but not same handle $id");
                if($remove){
                    igk_wln("unset frame");
                    unset($this->m_frames[$id]);
                    return true;
                }
                return false;
            }
            return true;
        }
        else{}
        return false;
    }
    ///<summary>Represente createFrame function</summary>
    ///<param name="id"></param>
    ///<param name="owner"></param>
    ///<param name="closeuri" default="null"></param>
    ///<param name="reloadcallback" default="null"></param>
    public function createFrame($id, $owner, $closeuri=null, $reloadcallback=null){
        if(($id == null) || !is_string($id))
            return null;
        if(isset($this->m_frames[$id])){
            $v_dial=$this->m_frames[$id];
            $b=$v_dial->getOwner();;
            if($b === $owner)
                return $v_dial;
        }
        $v_dial=new IGKMsDialogFrame($this, $id, $owner, $reloadcallback);
        $v_dial->ClearChilds();
        $cluri=null;
        if($closeuri){
            $cluri="&closeuri=".urlencode($closeuri);
        }
        else{
            $cluri="&navigate=1";
        }
        $v_dial->setCloseUri($this->getUri("closeFrame&id=".$id.$cluri));
        $v_dial["id"]=$id;
        $this->
        m_frames[$id]=$v_dial;
        return $v_dial;
    }
    ///<summary>Represente getFrame function</summary>
    ///<param name="id"></param>
    public function getFrame($id){
        if(isset($this->m_frames[$id])){
            return $this->m_frames[$id];
        }
        return null;
    }
    ///<summary>Represente getName function</summary>
    public function getName(){
        return IGK_FRAME_CTRL;
    }
    ///<summary>Represente IsFrameAvailable function</summary>
    ///<param name="id"></param>
    public function IsFrameAvailable($id){
        return isset($this->m_frames[$id]);
    }
    ///<summary>Represente View function</summary>
    public function View(){}
}
