final class IGKCacheCtrl extends IGKNonVisibleControllerBase {
    ///<summary>Represente __construct function</summary>
    public function __construct(){
        parent::__construct();
    }
    ///<summary>Represente __toString function</summary>
    public function __toString(){
        return "cachectrl";
    }
    ///<summary>Represente getCacheFile function</summary>
    ///<param name="name"></param>
    public function getCacheFile($name){
        return igk_io_cachedir().DIRECTORY_SEPARATOR.$name;
    }
    ///<summary>Represente getName function</summary>
    public function getName(){
        return "cachectrl";
    }
    ///<summary>Represente InitComplete function</summary>
    protected function InitComplete(){
        parent::InitComplete();
        $doc=igk_app()->doc;
        if($doc){
            $meta=igk_createnode("meta");
            $meta["http-equiv"]="Cache-control";
            $meta[IGKHtmlMetaManager::
            ATTR_CONTENT]="public";
            $doc->Metas->addMeta("CacheControl", $meta);
        }
    }
    ///<summary>Represente loadCache function</summary>
    ///<param name="name"></param>
    ///<param name="ctrl" default="null"></param>
    public function loadCache($name, $ctrl=null){
        $t=igk_getdv($this->App->Configs->cache_file_time, 3600);
        $expire=time() - $t;
        $f=$this->getCacheFile($name);
        if(file_exists($f) && (filemtime($f) > $expire)){
            readfile($f);
            return true;
        }
        else{
            if(($ctrl != null)){
                ob_start();
                $ctrl->View();
                $o=$ctrl->TargetNode->Render();
                igk_wl($o);
                ob_end_clean();
                igk_io_save_file_as_utf8($f, utf8_decode($o));
                igk_wl($o);
            }
        }
        return false;
    }
    ///<summary>Represente storeCache function</summary>
    ///<param name="name"></param>
    ///<param name="out"></param>
    public function storeCache($name, $out){
        $f=$this->getCacheFile($name);
        ob_start();
        igk_wl($out);
        ob_end_clean();
        igk_io_save_file_as_utf8($f, utf8_decode($out));
        igk_wl($out);
    }
}
