    }
    ///<summary>Represente getId function</summary>
    ///<param name="obj"></param>
    public function getId($obj){
        if($obj == null)
            return null;
        $r=$obj->getParam(__CLASS__.":id");
        if($r)
            return $r;
        foreach($this->m_ids as $k=>$v){
            if($v === $obj)
                return $k;
        }
        return null;
    }
    ///<summary>Represente getm_ids function</summary>
    public function getm_ids(){
        return $this->settings->ids;
    }
    ///<summary>Represente getm_objs function</summary>
    public function getm_objs(){
