                    Content=<<<EOF
(function(){ ns_igk.ajx.post('${uri}',null, ns_igk.ajx.fn.prepend_to_body); })();
EOF;
                    $d->RenderAJX();
                }
                else if($view){
                    $render=true;
                }
            }
        }
        else{
            $render=$this->HasMsg;
        }
        if($render){
            $this->TargetNode->RenderAJX();
            $this->
            m_hasmsg=false;
            $this->setParam("ajx:context", null);
