    ///<summary>Represente ClearAllS function</summary>
    public function ClearAllS(){
        igk_kill_all_sessions();
        $l=igk_sys_srv_referer();
        if(empty($l))
            $l=igk_io_baseuri();
        igk_navto($l);
    }
    ///<summary>Represente clearAllSession function</summary>
    public function clearAllSession(){
        if(igk_is_conf_connected()){
            $tab=igk_sys_get_all_openedsessionid();
            $cid=session_id();
            $c=0;
            foreach($tab as $k=>$v){
                if($k == $cid)
                    continue;
                unlink($v["file"]);
                $c++;
            }
            if($c > 0)
                igk_notifyctrl()->addMsg("Some session destroyed : ".$c);
        }
        igk_navtobaseuri();
    }
    ///<summary>Represente clearcache function</summary>
    //@exposed
    public function clearcache(){
        if(IGKServerInfo::IsLocal() || igk_is_conf_connected() || !igk_sys_env_production()){
            igk_clear_cache();
        }
        igk_navto_referer();
    }
    ///<summary> clear session and navigate</summary>
    public function ClearS($navigate=true){
        igk_session_destroy();
        session_write_close();
        $_SESSION=array();
        $_rcu=explode("?", igk_io_request_uri())[0];
        if($navigate){
            $buri=0;
            $s=$_rcu;
            if($s && IGKString::EndWith($s, "/clr")){
                $s=igk_str_rm_last($s, "/clr");
                $_rcu=empty($s) ? "/": $s;
                $buri=1;
            }
            $m=igk_getr("r");
            if($m){
                $m=base64_decode($m);
                igk_navto($m);
                igk_exit();
            }
            $u=igk_sys_srv_referer();
            if(!empty($u)){
                igk_navto_referer();
            }
            else{
                if($buri){
                    igk_navto($_rcu);
                }
            }
            igk_navto(igk_io_baseuri());
        }
    }
