        if(igk_get_env("sys://error"))
            return 0;
        return !defined('IGK_NO_WEB') && !igk_const_defined('IGK_NO_SESSION_BUTTON') && (IGKServerInfo::IsLocal() || (!IGKUserAgent::isMobileDevice() && igk_is_conf_connected() && igk_getv($this->App->Configs, "allow_debugging", false)));
    }
