    public function PageChanged(){
        $this->View();
    }
    ///<summary>Represente RunCron function</summary>
    public function RunCron(){
        $c=igk_getr("ctrl");
        if($c){
            $c=igk_getctrl($c);
            IGKControllerBase::RunCrons($c);
            igk_exit();
        }
        $tab=array();
        $server=igk_server()->HTTP_USER_AGENT;
        $sessid=igk_getv($_COOKIE, 'PHPSESSID', session_id());
        $strCookie='PHPSESSID='.$sessid.'; path='.igk_get_cookie_path();
        $f=igk_data_get_cron_file();
        if(file_exists($f))
            $tab=igk_json_parse(igk_io_read_allfile($f));
        else
            $tab[]=igk_io_baseuri().$this->getUri("RunCron&ctrl=baobabtv");
        $doc=igk_get_document(__METHOD__);
        session_write_close();
        IGKOb::Start();
        foreach($tab as $k=>$v){
            if(!IGKValidator::IsUri($v) || ($v[0] == "?")){
                $v=igk_io_baseuri().$v;
            }
            $r=curl_init();
            if($r){
                curl_setopt($r, CURLOPT_URL, $v);
                curl_setopt($r, CURLOPT_SSL_VERIFYPEER, false);
                curl_setopt($r, CURLOPT_SSL_VERIFYHOST, false);
                curl_setopt($r, CURLOPT_USERAGENT, $server);
                curl_setopt($r, CURLOPT_COOKIE, $strCookie);
                $data=curl_exec($r);
                curl_close($r);
            }
        }
        $d=IGKOb::Content();
        IGKOb::Clear();
        $doc->body->addBodyBox()->addDiv()->
        Content=$d;
        $doc->RenderAJX();
        $doc->Dispose();
        igk_exit();
    }
    ///<summary>Represente update_setting function</summary>
    public function update_setting(){
        $this->View();
    }
    ///<summary>Represente View function</summary>
    public function View(){
        $t=$this->TargetNode;
        if($t){
            if($t && $this->IsVisible){
                igk_html_add($t, $this->doc->body);
                $this->_viewTarget();
            }
            else{
                igk_html_rm($t);
            }
        }
    }
}
