        $f=$this->getConfigFile();
        if(file_exists($f)){
            $div=igk_createxmlnode("div");
            $s=igk_io_read_allfile($f);
            $div->Load($s);
            $d=igk_getv($div->getElementsByTagName("config"), 0);
            if($d){
                foreach($d->Childs as $k){
                    if($k->Type == "HtmlText")
                        continue;
                    if($k->ChildCount<=0){
                        $t->{$k->
                        TagName}
                        =$k->innerHTML;
                    }
                    else{
                        $v_ob=igk_createobj();
                        igk_conf_load($v_ob, $k);
                        $t->{$k->
                        TagName}
                        =$v_ob;
                    }
                }
            }
        }
        if($this->ctrl->getIsSystemController()){
            $t->
