        else{
            if(!isset($t->clRegisterName)){
                $t->
                clRegisterName=igk_sys_getconfig("website_prefix", "igk").".".$this->ctrl->getName();
            }
        }
        $this->
        m_changed=0;
        $this->
        m_configs=$t;
        return $t;
