        igk_die(__METHOD__." Not implement");
    }
    ///<summary>Represente storeConfig function</summary>
    public function storeConfig(){
        $d=igk_createxmlnode("config");
        $c=$this->m_configs;
        foreach($c as $k=>$v){
            igk_conf_store_value($d, $k, $v);
