///<summary> System Controllers Managers. store list of different controller table. </summary>
///<note></note>
final class IGKControllerManagerObject extends IGKObject {
    private $m_classReg;
    private $m_initEvent;
    private $m_register;
    private $m_registeredCtrl;
    private $m_tbcontrollers;
    private $m_tbviewcontrollers;
    static private $sm_instance;
    ///<summary>Represente __construct function</summary>
    private function __construct(){
        $this->
        m_tbcontrollers=array();
        $this->
        m_registeredCtrl=array();
        $this->
        m_tbviewcontrollers=array();
        $this->
        m_initEvent=0;
    }
    ///<summary>Represente __get function</summary>
    ///<param name="key"></param>
    public function __get($key){
        $key=strtolower($key);
        $c=igk_getv($this->m_tbcontrollers, $key);
        if($c){
            if(igk_is_class_incomplete($c)){
                unset($this->m_tbcontrollers[$key]);
                return null;
            }
            return $c;
        }
        return null;
    }
    ///<summary>Represente __set function</summary>
    ///<param name="key"></param>
    ///<param name="value"></param>
    public function __set($key, $value){
        $key=strtolower($key);
        if($value === null){
            if(isset($this->m_tbcontrollers[$key]))
                unset($this->m_tbcontrollers[$key]);
            return;
        }
        else{
            if(is_object($value) && igk_reflection_class_extends(get_class($value), IGK_CTRLBASECLASS)){
                $this->
                m_tbcontrollers[$key]=$value;
            }
        }
    }
    ///<summary>Represente __toString function</summary>
    public function __toString(){
