            $tab_k=array_keys($tab);
            igk_usort($tab_k, "igk_key_sort");
            foreach($tab_k as $k){
                $v=$tab[$k];
                if(get_class($v) === '__PHP_Incomplete_Class'){
                    unset($tab[$k]);
                    continue;
                }
                if(IGKControllerManagerObject::IsSystemController($v) || IGKControllerManagerObject::IsIncludedController($v) || !$v->canModify)
