///<summary>Represente class: IGKDataAdapter</summary>
abstract class IGKDataAdapter extends IGKObject {
    static private $sm_regAdapter=null;
    ///<summary>Represente ClearTable function</summary>
    ///<param name="tablename"></param>
    public function ClearTable($tablename){}
    ///<summary>Represente close function</summary>
    abstract public function close();
    ///<summary>Represente configure function</summary>
    ///<param name="params"></param>
    protected function configure($params){}
    ///<summary>Represente connect function</summary>
    ///<param name="ctrl" default="null"></param>
    abstract public function connect($ctrl=null);
    ///<summary>Represente CreateDataAdapter function</summary>
    ///<param name="ctrl"></param>
    ///<param name="throwexception"></param>
    ///<param name="newAdapter"></param>
    ///<param name="params" default="null"></param>
    static public function CreateDataAdapter($ctrl, $throwexception, $newAdapter=0, $params=null){
        $adapt=self::GetAdapters();
        $n=IGK_STR_EMPTY;
        $key=IGK_STR_EMPTY;
        if(is_string($ctrl)){
            $key=strtoupper($ctrl);
            $n="IGK".$ctrl."DataAdapter";
        }
        else{
            $key=strtoupper($ctrl->getDataAdapterName());
            $n="IGK".$key."DataAdapter";
        }
        if(!$newAdapter && isset($adapt[$key])){
            return $adapt[$key];
        }
        if(IGKApp::$DEBUG)
            igk_debug_wln("CreateDataApter: not found .... ".$ctrl. ":".$n. " :::: ".$ctrl->getDataAdapterName());
        if(class_exists($n) && !igk_reflection_class_isabstract($n)){
            $out=igk_create_adapter_from_classname($n);
            if($out){
                $adapt[$key]=$out;
                return $out;
            }
        }
        else{
            $c=igk_get_env("sys://dataadapter");
            if($c && isset($c[$key])){
                $o=$c[$key];
                $out=new $o();
                $adapt[$key]=$out;
                return $out;
            }
        }
        if($throwexception){
            igk_die($n. " not found : Controller : ".$ctrl);
        }
        return null;
    }
    ///<summary>Represente CreateEmptyResult function</summary>
    abstract public function CreateEmptyResult();
    ///<summary>Represente createTable function</summary>
    ///<param name="tablename"></param>
    ///<param name="columninfoArray"></param>
    ///<param name="entries" default="null"></param>
    public function createTable($tablename, $columninfoArray, $entries=null){}
    ///<summary>Represente delete function</summary>
    ///<param name="tablename"></param>
    ///<param name="entries"></param>
    public function delete($tablename, $entries){}
    ///<summary>Represente deleteAll function</summary>
    ///<param name="tablename"></param>
    public function deleteAll($tablename){
        igk_die("function ".__FUNCTION__." not implements");
    }
    ///<summary>Represente drop function</summary>
    public function drop(){
        $this->deleteAll();
    }
    ///<summary>Represente flushForInitDb function</summary>
    public function flushForInitDb(){}
    ///<summary>Represente GetAdapters function</summary>
    static public function GetAdapters(){
        if(self::$sm_regAdapter == null){
            self::
            $sm_regAdapter=array();
            self::LoadAdapter();
        }
        return self::$sm_regAdapter;
    }
    ///<summary>Represente getApp function</summary>
    public function getApp(){
        return igk_app();
    }
    ///<summary>get the db identifier</summary>
    public function getDbIdentifier(){
        return "db";
    }
    ///<summary>Represente getIsAvailable function</summary>
    public function getIsAvailable(){
        return true;
    }
    ///<summary>Represente initForInitDb function</summary>
    public function initForInitDb(){}
    ///DATA ADAPTER OVERRIDABLE FUNCTIONS
    public function insert($table, $entries){
        igk_wln(__CLASS__. " - [warning] :::: must override insert");
        return false;
    }
    ///<summary>Represente Load function</summary>
    static public function Load(){
        if(defined("IGK_INIT"))
            return;
        self::LoadAdapter();
    }
    ///<summary>Represente LoadAdapter function</summary>
    static private function LoadAdapter(){
        $fc=igk_io_syspath(IGK_ADAPTER_CACHE);
        $n="/^IGK(?P<name>(.)+)DataAdapter$/i";
        if(empty($fc)){
            return false;
        }
        if(file_exists($fc)){
            foreach(explode(IGK_LF, igk_io_read_allfile($fc)) as $k){
                if(empty(trim($k)))
                    continue;
                $key=strtoupper($k);
                if(preg_match_all($n, $key, $tab)){
                    $key=$tab["name"][0];
                }
                self::
                $sm_regAdapter[$key]=new $k();
            }
        }
        else{
            self::
            $sm_regAdapter=array();
            $m="";
            foreach(get_declared_classes() as $k=>$v){
                if(preg_match($n, $v)){
                    $t=array();
                    preg_match_all($n, $v, $t);
                    $s=$t["name"][0];
                    if(!igk_reflection_class_isabstract($v) && igk_reflection_class_extends($v, "IGKDataAdapter")){
                        self::$sm_regAdapter[strtoupper($s)
                        ]=new $v();
                        $m .= $v.IGK_LF;
                    }
                }
            }
            igk_io_w2file($fc, $m);
        }
    }
    ///<summary>Represente makeCurrent function</summary>
    public function makeCurrent(){
        IGKSQLManager::
        $Config["db"]=$this->getDbIdentifier();
    }
    ///<summary> override to manage the open connexion counter</summary>
    public function OpenCount(){
        return 0;
    }
    ///<summary>Represente ResetDataAdapter function</summary>
    static public function ResetDataAdapter(){
        self::
        $sm_regAdapter=array();
        self::LoadAdapter();
    }
    ///<summary>Represente select function</summary>
    ///<param name="tablename"></param>
    ///<param name="entries"></param>
    public function select($tablename, $entries){}
    ///<summary>Represente selectAll function</summary>
    ///<param name="tbname"></param>
    public function selectAll($tbname){
        return null;
    }
    ///<summary>Represente selectAndWhere function</summary>
    ///<param name="tablename"></param>
    ///<param name="conditions" default="null"></param>
    ///<param name="options" default="null"></param>
    public function selectAndWhere($tablename, $conditions=null, $options=null){
        igk_die("function ".__FUNCTION__." not implements");
    }
    ///<summary>Represente selectdb function</summary>
    ///<param name="dbname"></param>
    public function selectdb($dbname){}
    ///<summary>Represente selectLastId function</summary>
    public function selectLastId(){
        return null;
    }
    ///<summary>Represente update function</summary>
    ///<param name="tablename"></param>
    ///<param name="entrie"></param>
    public function update($tablename, $entrie){}
}
///<summary>Represente class: IGKCSVDataAdapter</summary>
final class IGKCSVDataAdapter extends IGKDataAdapter {
    private $m_ctrl;
    private $m_dbname;
    private $m_fhandle;
    ///<summary>Represente __construct function</summary>
    ///<param name="ctrl" default="null"></param>
    public function __construct($ctrl=null){
        $this->
        m_ctrl=$ctrl;
    }
    ///<summary>Represente __removeguillemet function</summary>
    ///<param name="data"></param>
    static function __removeguillemet($data){
        if(IGKString::StartWith($data, '"')){
            $data=substr($data, 1);
        }
        if(IGKString::EndWith($data, '"'))
            $data=substr($data, 0, strlen($data)-1);
        return $data;
    }
    ///<summary>Represente _CSVReadLine function</summary>
    ///<param name="line"></param>
    static private function _CSVReadLine($line){
        $tab=array();
        $rtab=explode(igk_csv_sep(), $line);
        $v=false;
        $r=IGK_STR_EMPTY;
        foreach($rtab as $k=>$i){
            $s=trim($i);
            if($v == false){
                if(IGKString::StartWith($s, '"')){
                    $v=true;
                    $r .= substr($s, 1);
                    if(IGKString::EndWith($r, '"')){
                        $v=false;
                        $r=igk_str_rm_last($r, '"');
                    }
                }
                else
                    $r .= $i;
            }
            else{
                if(IGKString::EndWith($s, '"')){
                    $r .= igk_csv_sep().igk_str_rm_last($s, '"');
                    $v=false;
                }
                else
                    $r .= igk_csv_sep().$i;
            }
            if($v == false){
                $tab[]=trim($r);
                $r=IGK_STR_EMPTY;
            }
        }
        return $tab;
    }
    ///<summary>Represente close function</summary>
    public function close(){
        if($this->m_fhandle){
            fclose($this->m_f);
        }
    }
    ///<summary>Represente connect function</summary>
    ///<param name="datafile" default="file"></param>
    public function connect($datafile="file"){
        $this->
        m_dbname=$datafile;
    }
    ///<summary>Represente countAndWhere function</summary>
    public function countAndWhere(){
        igk_wln_e(igk_show_trace());
    }
    ///<summary>Represente CreateEmptyResult function</summary>
    ///<param name="result" default="null"></param>
    public function CreateEmptyResult($result=null){
        return null;
    }
    ///<summary>Represente GetValue function</summary>
    ///<param name="value"></param>
    static public function GetValue($value){
        if((strpos($value, igk_csv_sep()) !== false) || preg_match("/ /i", $value))
            return "\"".$value."\"";
        return $value;
    }
    ///<summary>Represente initSystablePushInitItem function</summary>
    ///<param name="tablename"></param>
    ///<param name="callback"></param>
    public function initSystablePushInitItem($tablename, $callback){}
    ///<summary>Represente initSystableRequired function</summary>
    ///<param name="tablename"></param>
    public function initSystableRequired($tablename){
        return false;
    }
    ///<summary>Represente LoadData function</summary>
    ///<param name="filename"></param>
    static public function LoadData($filename){
        $txt=IGKIO::ReadAllText($filename);
        return self::LoadString($txt);
    }
    ///<summary>Represente LoadString function</summary>
    ///<param name="txt"></param>
    ///<param name="rmBom" default="true"></param>
    static public function LoadString($txt, $rmBom=true){
        if(empty($txt))
            return array();
        if($rmBom){
            $txt=igk_io_remove_bom($txt);
        }
        $lines=explode(IGK_LF, $txt);
        $entries=array();
        foreach($lines as $l){
            if(empty($l)){
                continue;
            }
            $tab=self::_CSVReadLine($l);
            $entries[]=$tab;
        }
        return $entries;
    }
    ///<summary>Represente selectAll function</summary>
    ///<param name="tbname"></param>
    public function selectAll($tbname){
        $this->selectAllFile($tbname);
    }
    ///<summary>Represente selectAllFile function</summary>
    ///<param name="tbname"></param>
    public function selectAllFile($tbname){
        $f=igk_io_basepath(IGK_DATA_FOLDER)."/".$tbname.".csv";
        if(file_exists($f)){
            $r=IGKCSVQueryResult::CreateEmptyResult();
            $r->AppendEntries(self::LoadData($f), $this->m_ctrl->getDataTableInfo());
            return $r;
        }
        return null;
    }
    ///<summary>Represente StoreData function</summary>
    ///<param name="filename"></param>
    ///<param name="entries"></param>
    static public function StoreData($filename, $entries){
        $out=IGK_STR_EMPTY;
        $v_ln=false;
        foreach($entries as $k=>$v){
            if($v_ln){
                $out .= IGK_LF;
            }
            else
                $v_ln=true;
            $v_sep=false;
            foreach($v as $d){
                if($v_sep){
                    $out .= igk_csv_sep();
                }
                else
                    $v_sep=true;
                $out .= self::GetValue($d);
            }
        }
        igk_io_save_file_as_utf8($filename, $out, true);
    }
    ///convert tab to line entry
    static public function toCSVLineEntry($tab, $key=null){
        $out=IGK_STR_EMPTY;
        $v_sep=false;
        if(is_object($tab)){
            foreach($tab as $k=>$c){
                if($v_sep)
                    $out .= igk_csv_sep();
                else
                    $v_sep=true;
                $out .= self::GetValue($c);
            }
            return $out;
        }
        if(!is_array($tab)){
            return null;
        }
        if($key != null){
            $v_sep=false;
            foreach($tab as $c){
                if($v_sep)
                    $out .= igk_csv_sep();
                else
                    $v_sep=true;
                $out .= $c->$key;
            }
        }
        else{
            foreach($tab as $c){
                if($v_sep)
                    $out .= igk_csv_sep();
                else
                    $v_sep=true;
                $out .= self::GetValue($c);
            }
        }
        return $out;
    }
}
///<summary>Represente class: IGKSQLDataAdapter</summary>
abstract class IGKSQLDataAdapter extends IGKDataAdapter{
    ///<summary>Represente delete function</summary>
    ///<param name="tbname"></param>
    ///<param name="entry"></param>
    public function delete($tbname, $entry){
        $this->dieNotConnect();
        $query=IGKSQLQueryUtils::GetDeleteQuery($tbname, $entry);
        $t=$this->sendQuery($query, $tbname);
        if($t){
            return true;
        }
        return false;
    }
    ///<summary>delete all from table</summary>
    public function deleteAll($tbname){
        return $this->sendQuery("DELETE FROM `".igk_mysql_db_tbname($tbname)."`", $tbname);
    }
    ///<summary>setup manager config for next operation</summary>
    protected function initConfig(){}
    ///<summary>Represente insert function</summary>
    ///<param name="tbname"></param>
    ///<param name="values"></param>
    ///<param name="tableinfo" default="null"></param>
    public function insert($tbname, $values, $tableinfo=null){
        $this->dieNotConnect();
        $this->initConfig();
        $query=IGKSQLQueryUtils::GetInsertQuery($tbname, $values, $tableinfo);
        igk_wln_e($query);
        $t=$this->sendQuery($query, $tbname);
        if($t){
            if(is_object($values)){
                if(igk_getv($values, "clId") == null)
                    $values->
                clId=$this->last_id();
            }
            return true;
        }
        else{
            $error="Insertion Query Error : ".igk_mysql_db_error(). " : ".$query;
            igk_debug_wln($error);
            igk_db_error($error);
        }
        return false;
    }
    ///<summary>Represente last_id function</summary>
    public function last_id(){}
    ///<summary>build and send a mysql select query</summary>
    ///<param name="options">callback or igk_db_create_opt_obj()</param>
    public function select($tbname, $where=null, $options=null){
        if(empty($tbname))
            igk_die("table is empty");
        $q=IGKSQLQueryUtils::GetSelectQuery($tbname, $where, $options);
        return $this->sendQuery($q, $tbname, $options);
    }
    ///<summary>Represente selectAll function</summary>
    ///<param name="tbname"></param>
    public function selectAll($tbname){
        $tbname=igk_mysql_db_tbname($tbname);
        $q="SELECT * FROM `".($tbname)."` ";
        return $this->sendQuery($q, $tbname);
    }
    ///<summary>Represente selectAndWhere function</summary>
    ///<param name="tbname"></param>
    ///<param name="condition" default="null"></param>
    ///<param name="options" default="null"></param>
    public function selectAndWhere($tbname, $condition=null, $options=null){
        $o="";
        $q="";
        $s=0;
        $s="";
        $column=IGKSQLQueryUtils::GetColumnList($options, $tbname);
        if(is_string($tbname)){
            $tbname=igk_mysql_db_tbname($tbname);
            $q="SELECT {$column} FROM `".$tbname."`";
        }
        else if(is_array($tbname)){
            $r=0;
            foreach($tbname as $k){
                $_n=igk_mysql_db_tbname($k);
                if($r)
                    $q .= ",";
                $q .= "`".$_n."`";
                $r=1;
            }
            $q="SELECT * FROM ".$q."";
        }
        if($condition){
            if(is_object($condition)){
                $q .= " WHERE ".IGKSQLQueryUtils::GetCondString($condition);
            }
            else if(is_array($condition) && igk_count($condition) > 0){
                $q .= " WHERE ".IGKSQLQueryUtils::GetCondString($condition);
            }
            else if(is_numeric($condition) || !is_string($condition)){
                $q .= " WHERE `".IGK_FD_ID."`='".igk_db_escape_string($condition)."' ";
            }
            else if(is_string($condition)){
                $q .= " WHERE ".igk_db_escape_string($condition)." ";
            }
        }
        $q .= $options ? IGKSQLQueryUtils::GetExtraOptions($options): "";
        if(igk_is_debug()){
            igk_wln_e("query: ", $q);
        }
        return $this->sendQuery($q, $tbname, $options);
    }
    ///<summary>Represente update function</summary>
    ///<param name="tbname"></param>
    ///<param name="entry"></param>
    ///<param name="condition" default="null"></param>
    ///<param name="tabinfo" default="null"></param>
    public function update($tbname, $entry, $condition=null, $tabinfo=null){
        $this->dieNotConnect();
        $query=IGKSQLQueryUtils::GetUpdateQuery($tbname, $entry, $condition, $tabinfo);
        $s=$this->sendQuery($query, $tbname);
        return $s;
    }
}
