    ///<param name="node"></param>
    static public function LoadConfig($node){
        if(($node == null) || (!$node->HasChilds))
            return null;
        $t=array();
        foreach($node->Childs as $k){
            $t[$k->
            TagName]=$k->innerHTML;
        }
        return $t;
    }
    ///load data file name of text
    static public function LoadData($filename){
        $fc=null;
        $fc=function($node) use (& $fc){
            if($node->HasChilds){
                if($node->ChildCount == 1){
                    if($node->Childs[0]->NodeType == XmlNodeType::TEXT){
                        return $node->innerHTML;
                    }
                    else{
                        $t=array();
                        $t[$node->
                        TagName]=$fc($node);
                        return (object)$t;
                    }
                }
                else{
                    $t=array();
                    foreach($node->Childs as $k){
                        if(isset($t[$k->TagName])){
                            $v=$t[$k->TagName];
                            $s=array();
                            $s[]=$v;
                            $s[]=$fc($k);
                            $t[$k->
                            TagName]=$s;
                        }
                        else
                            $t[$k->
                        TagName]=$fc($k);
                    }
                    return (object)$t;
                }
            }
            return null;
        };
        $f=$filename;
        if(is_string($f) == false)
            return null;
        $div=igk_createnode("div");
        $s=IGK_STR_EMPTY;
        if(file_exists($f)){
            $s=igk_io_read_allfile($f);
        }
        else{
            $s=$f;
        }
        $div->Load($s);
        if($div->HasChilds){
            $d=$div->getChildAtIndex(0);
            if($d){
                $t=array();
                foreach($d->Childs as $k){
                    $t[$k->
                    TagName]=__loadData($k);
                }
                return (object)$t;
            }
        }
        return null;
    }
    ///<summary>Represente storeData function</summary>
    ///<param name="filename"></param>
    ///<param name="data"></param>
    static public function storeData($filename, $data){
        $d=igk_createnode("config");
        foreach($data as $k=>$v){
            $d->add($k)->
            Content=$v;
        }
        return igk_io_save_file_as_utf8(filename, $d->Render());
    }
}
