    ///<summary>Represente __toString function</summary>
    public function __toString(){
        return "IGKDateTime:[".$this->day."-".$this->month."-".$this->year."]";
    }
    ///<summary>Represente compareDate function</summary>
    ///<param name="date1"></param>
    ///<param name="date2"></param>
    static public function compareDate($date1, $date2){
        if(!$date1 || !$date2)
            return -2;
        $s1=$date1->getDate("Ymd");
        $s2=$date2->getDate("Ymd");
        return strcmp($s1, $s2);
    }
    ///<summary>Represente CreateFrom function</summary>
    ///<param name="format"></param>
    ///<param name="value"></param>
    static public function CreateFrom($format, $value){
        $tab=(object)date_parse_from_format($format, $value);
        if($tab->error_count == 0){
            $d=new IGKDateTime();
            $d->
            m_day=$tab->day;
            $d->
            m_month=$tab->month;
            $d->
            m_year=$tab->year;
            $d->
            m_min=$tab->minute;
            $d->
            m_sec=$tab->second;
            $d->
            m_hour=$tab->hour;
            return $d;
        }
        else{
            if(IGKApp::$DEBUG)
                igk_show_prev($tab);
        }
        return null;
    }
    ///<summary>Represente getDate function</summary>
    ///<param name="format"></param>
    public function getDate($format){
        $s=$format;
        $s=str_replace("Y", $this->year, $s);
        $s=str_replace("m", $this->month, $s);
        $s=str_replace("d", $this->day, $s);
        $s=str_replace("H", $this->hour, $s);
        $s=str_replace("i", $this->min, $s);
        $s=str_replace("s", $this->sec, $s);
        return $s;
    }
    ///<summary>Represente getday function</summary>
    public function getday(){
        return $this->m_day;
    }
    ///<summary>Represente gethour function</summary>
    public function gethour(){
