    }
    ///<summary>Represente getmin function</summary>
    public function getmin(){
        return $this->m_min;
    }
    ///<summary>Represente getmonth function</summary>
    public function getmonth(){
        return $this->m_month;
    }
    ///<summary>Represente getsec function</summary>
    public function getsec(){
        return $this->m_sec;
    }
    ///<summary>Represente getyear function</summary>
    public function getyear(){
        return $this->m_year;
    }
    ///<summary>Represente isDateEqual function</summary>
    ///<param name="date1"></param>
    ///<param name="date2"></param>
    static public function isDateEqual($date1, $date2){
        return self::compareDate($date1, $date2) == 0;
    }
    ///<summary>Represente isDateMonthEqual function</summary>
    ///<param name="date1"></param>
    ///<param name="date2"></param>
    static public function isDateMonthEqual($date1, $date2){
        return (self::IsDateYearEqual($date1, $date2) === true) && ($date1->month == $date2->month);
    }
    ///<summary>Represente isDateYearEqual function</summary>
    ///<param name="date1"></param>
    ///<param name="date2"></param>
    static public function isDateYearEqual($date1, $date2){
        if(!$date1 || !$date2)
            return -2;
        return $date1->year == $date2->year;
    }
}
