            }
            else{
                igk_die("not implement ".$r->RowCount);
            }
        };
        if($condition == null){
            $tab=null;
            if(igk_db_data_is_present($this->Ctrl, $table, $obj, null, $tab)){
                return $_invoke($tab);
            }
            if($this->insert($table, $obj))
                return 1;
        }
        else{
            $r=$this->select($table, $condition);
            if($r->RowCount > 0){
                return $_invoke($r);
            }
            else{
                if($this->insert($table, $obj))
                    return 1;
            }
        }
        return 0;
    }
