    ///<summary>Represente offsetExists function</summary>
    ///<param name="key"></param>
    function offsetExists($key){
        if(is_object($key))
            igk_die("offsetExists :: keys is object ");
        $g=$this->getAttributes();
        return isset($g[$key]);
    }
    ///<summary>Represente offsetGet function</summary>
    ///<param name="key"></param>
    public function offsetGet($key){
        $g=$this->getAttributes();
        if($g && isset($g[$key]))
            return $g[$key];
        return null;
    }
    ///<summary>Represente offsetSet function</summary>
    ///<param name="key"></param>
    ///<param name="value"></param>
    function offsetSet($key, $value){
        $o=$this->getOwner();
        switch(strtolower($key)){
            case "class":{
                $attr=$this->getAttributes();
                if($value === null){
                    unset($attr[$key]);
                }
                else{
                    if(get_class($o) === IGKXmlNode::class){
                        $attr[$key]=$value;
                    }
                    else{
                        $g=igk_getv($attr, $key);
                        if(($g == null) || !is_object($g)){
                            $g=new IGKHtmlClassValueAttribute($this);
                            $attr[$key]=$g;
                        }
                        $g->add($value);
                    }
                }
                $this->_f->updateFlag(self::ATTRIBS, $attr);
            }
            break;
            case "rmclass":
            $tb=explode(" ", $value);
            foreach($tb as $k=>$v){
                $v=trim($v);
                if(empty($v))
                    continue;
                $this->offsetSet("class", "-".$v);
            }
            break;default: 
            if(IGKString::StartWith($key, 'igk:')){
                $ck=substr($key, 4);
                if($ck == "param"){
                    return;}
                if(!$o->setSysAttribute($ck, $value, $o->LoadingContext)){
                    $this->offsetSetExpression($key, $value);
                }
            }
            else{
                $this->offsetSetExpression($key, $value);
            }
            break;
        }
    }
    ///<summary>Represente offsetSetExpression function</summary>
    ///<param name="key"></param>
    ///<param name="value"></param>
    function offsetSetExpression($key, $value){
        if(preg_match("/^@igk:expression/", $key)){
            $g=$this->getAttributes() ?? $this->_initattribs();
            if($g){
                if($value === null)
                    unset($g[$key]);
                else
                    $g[$key]=new IGKHtmlExpressionAttribute($value);
            }
            return;
        }
        $this->Set($key, $value);
    }
    ///<summary>Represente offsetUnset function</summary>
    ///<param name="key"></param>
    function offsetUnset($key){
        $g=$this->getAttributes();
        if($g){
            unset($g[$key]);
            $this->_f->freeFlag(self::ATTRIBS);
        }
    }
    ///<summary>Represente rewind function</summary>
    function rewind(){
        $attr=$this->getAttributes() ?? array();
        $g=$this->_f->getFlag(self::ACTIVATE) ?? array();
        if($g){
            $attr["@activated"]=$g;
        }
        $o=igk_createobj();
        $o->
        it_vtab=$attr;
        $o->
        it_index=0;
        $o->
        it_total=igk_count($o->it_vtab);
        $o->
        it_keys=$o->it_vtab ? array_keys($o->it_vtab): null;
        if($o->it_total > 0)
            $o->
        it_key=$o->it_keys[0];
        else
            $o->
        it_key=null;
        $this->_f->setFlag(self::ITERATOR, $o);
    }
    ///<summary>set the attribute</summary>
    ///<remark>if value is null then the value will be unset</remark>
    function Set($key, $value){
        if(preg_match("/^xmlns(:(?P<prefix>(.)+)){0,1}$/", trim($key), $tab)){
            $ns=$this->_f->getFlag(IGK_DEFINEDNS_FLAG) ?? array();
            if($value !== null){
                if(isset($tab["prefix"])){
                    $ns[$tab["prefix"]]=$value;
                }
                else{
                    $ns["@global"]=$value;
                }
            }
            else{
                if(isset($tab["prefix"])){
                    unset($ns[$tab["prefix"]]);
                }
                else
                    unset($ns["@global"]);
            }
            if(igk_count($ns)){
                $this->_f->setFlag(IGK_DEFINEDNS_FLAG, $ns);
            }
            else{
                $this->_f->unsetFlag(IGK_DEFINEDNS_FLAG);
            }
            return;
        }
        $g=$this->getAttributes() ?? $this->_initattribs();
        if($value === null){
            unset($g[$key]);
        }
        else
            $g[$key]=$value;
        $this->_f->updateFlag(self::ATTRIBS, $g);
    }
