final class IGKHtmlClassValueAttribute extends IGKHtmlItemAttribute implements Serializable {
    private $m_classes;
    private $m_expressions;
    static private $sm_regClass=null;
    ///<summary>Represente __construct function</summary>
    public function __construct(){
        $this->
        m_classes=array();
        $this->
        m_expressions=array();
    }
    ///<summary>Represente _add function</summary>
    ///<param name="v"></param>
    private function _add($v){
        if(is_array($v)){
            igk_die("is array");
        }
        $v=trim($v);
        if(strlen($v) > 0){
            switch($v[0]){
                case '-':
                $v=substr($v, 1);
                $this->remove($v);
                break;
                case '+':
                $v=substr($v, 1);
                if(!isset($this->m_classes[$v])){
                    $this->
                    m_classes[$v]=$v;
                }
                self::_RegClass(".".$v);
                break;
                case "[":
                case "{":
                $this->
                m_expressions[]=$v;
                break;default: 
                if(!isset($this->m_classes[$v])){
                    $this->
                    m_classes[$v]=$v;
                }
                self::_RegClass(".".$v);
                break;
            }
        }
    }
    ///<summary>Represente _GetRegClass function</summary>
    ///<return refout="true"></return>
    static private function & _GetRegClass(){
        if(self::$sm_regClass === null){
            if(igk_app()->Session->RegClasses !== null){
                self::
                $sm_regClass=& igk_app()->Session->RegClasses->regClass;
            }
            else{
                self::
                $sm_regClass=array();
            }
        }
        return self::$sm_regClass;
    }
    ///<summary>Represente _initThemeDef function</summary>
    ///<param name="App"></param>
    ///<param name="name"></param>
    static private function _initThemeDef($App, $name){
        $tab=array();
        $c=preg_match_all("/^\.(?P<type>(bgcl|fcl|res|ft))\-(?P<name>(.)+)$/i", $name, $tab);
        if($c > 0){
            $def=$App->Doc->Theme->def;
            for($i=0; $i < $c; $i++){
                $t=strtolower($tab['type'][$i]);
                $n=strtolower($tab['name'][$i]);
                $def[$name]="[$t:$n]";
            }
        }
    }
    ///<summary>Represente _RegClass function</summary>
    ///<param name="name"></param>
    static private function _RegClass($name){
        $v=& self::_GetRegClass();
        $app=igk_app();
        $doc=$app->getDoc();
        if(($doc == null) || isset($doc->SysTheme->def[$name]) || (isset($doc->Theme->def[$name]))){
            return;}
        if(!isset($v[$name])){
            $v[$name]=$name;
            self::_initThemeDef($app, $name);
            igk_invoke_session_event(IGKApp::$REG_CSS_CLASS_EVT, array($app, $name));
        }
    }
    ///<summary>Represente _UnRegClass function</summary>
    ///<param name="name"></param>
    static private function _UnRegClass($name){
        $v=& self::_GetRegClass();
        if(isset($v[$name])){
            unset($v[$name]);
            igk_invoke_session_event(IGKApp::$REG_CSS_CLASS_EVT, array($App, null));
        }
    }
    ///<summary>add css class value</summary>
    ///<param name="class">mixed string expression or array defenition</param>
    public function add($class){
        if(empty($class))
            return;
        $tab=null;
        if(is_array($class)){
            $cl=[];
            foreach($class as $k=>$v){
                if(is_callable($v)){
                    if($v()){
                        $cl[]=$k;
                    }
                    else{
                        $cl[]="-".$k;
                    }
                }
                else if($v){
                    $cl[]=$k;
                }
                else
                    $cl[]="-".$k;
            }
            $tab=$cl;
            $class=implode(" ", $cl);
        }
        else
            $tab=explode(" ", $class);
        if(count($tab) == 1){
            $this->_add($class);
        }
        else{
            foreach($tab as $v){
                $this->_add($v);
            }
        }
    }
    ///<summary>Represente Clear function</summary>
    public function Clear(){
        $this->
        m_expression=array();
        $this->
        m_classes=array();
    }
    ///get if this instance contain classe name
    public function contain($name){
        return isset($this->m_classes[$name]);
    }
    ///<summary>Represente EvalClassStyle function</summary>
    public function EvalClassStyle(){
        $out=IGK_STR_EMPTY;
        $i=0;
        foreach($this->m_classes as $k=>$v){
            if($i == 0)
                $i=1;
            else
                $out .= " ";
            $out .= igk_css_get_style(".".$v);
        }
        return $out;
    }
    ///<summary>Represente getKeys function</summary>
    public function getKeys(){
        return array_keys($this->m_classes);
    }
    ///<summary>Represente GetParentClass function</summary>
    ///<param name="theme"></param>
    ///<param name="v"></param>
    static private function GetParentClass($theme, $v){
        $s=$theme[$v];
        if(!empty($s)){
            $t=array();
            if(preg_match_all(IGK_CSS_CHILD_EXPRESSION_REGEX, $s, $t)){
                $vv=$t["name"][0];
                if(self::IsCssChild($vv)){
                    return self::GetParentClass($theme, $vv);
                }
                return $vv;
            }
        }
        return $v;
    }
    ///<summary>Represente GetRegClass function</summary>
    static public function GetRegClass(){
        return self::_GetRegClass();
    }
    ///<summary>get html css class presentation value</summary>
    public function getValue($options=null){
        $out=IGK_STR_EMPTY;
        $i=0;
        foreach($this->m_classes as $k=>$v){
            if($i == 0)
                $i=1;
            else
                $out .= " ";
            if(self::IsCssChild($v)){
                $out .= self::GetParentClass(igk_app()->Doc->Theme, $v);
            }
            else
                $out .= $v;
        }
        $b=IGKHtmlUtils::GetValue($out);
        return empty($b) ? null: $b;
    }
    ///<summary>Represente IsCssChild function</summary>
    ///<param name="v"></param>
    static public function IsCssChild($v){
        $c=igk_app();
        if($c && $c->Doc){
            $s=$c->Doc->Theme[$v];
            if(!empty($s)){
                $r=preg_match(IGK_CSS_CHILD_EXPRESSION_REGEX, trim($s));
                return $r;
            }
        }
        return false;
    }
    ///<summary>Represente remove function</summary>
    ///<param name="class"></param>
    public function remove($class){
        if(empty($class))
            return;
        if(isset($this->m_classes[$class])){
            unset($this->m_classes[$class]);
        }
    }
    ///<summary>Represente serialize function</summary>
    public function serialize(){
        if(igk_get_env("seri")){
            igk_die(__CLASS__.":: loop detected ::: ".__METHOD__);
            igk_exit();
        }
        igk_set_env("seri", 1);
        $s='v:'.implode(" ", array_keys($this->m_classes)).';';
        igk_set_env("seri", null);
        return $s;
    }
    ///<summary>Represente UnRegClass function</summary>
    ///<param name="key"></param>
    static public function UnRegClass($key){
        self::_UnRegClass($key);
    }
    ///<summary>Represente unserialize function</summary>
    ///<param name="data"></param>
    public function unserialize($data){
        $o=igk_unseri_data($data);
        $tab=explode(" ", $o->v);
        $this->
        m_classes=array_combine($tab, $tab);
        $r=igk_getv($o, "r");
        if($r){
            $owner=igk_get_env("sys://serialize/owner");
            $v='O:8:"stdClass":1:{s:5:"value";r:1;}';
        }
    }
