    ///<summary>Represente GetStringAttribute function</summary>
    ///<param name="v"></param>
    ///<param name="options"></param>
    static public function GetStringAttribute($v, $options){
        if(empty($v) && !is_numeric($v))
            return null;
        $data=null;
        while(is_object($v)){
            $v=IGKHtmlUtils::GetValueObj($v, $options);
        }
        if(empty($v) && !is_numeric($v)){
            return null;
        }
        if(is_string($v)){
            if(IGKString::StartWith($v, "\"")){
                return $v;
            }
            if(IGKString::StartWith($v, "\'"))
                return $v;
        }
        if($options && igk_getv($options, "AttributeEntityEscape")){
            $v=preg_replace_callback("/\&([^;=]+;)?/i", function($m){
                switch($m[0]){
                    case "&":
                    return "&amp;";
                    case "&copy;":
                    return "&#169;";
                    break;
                }
                return $m[0];
            }
            , $v);
        }
        $v=str_replace("\"", "&quot;", $v);
        if(is_array($v)){
            igk_wln_e(__METHOD__.":: attribute is array", igk_show_trace());
        }
        return "\"".$v."\"";
    }
